base_explicit";b:0;s:8:"xml_lang";s:0:"";s:5:"xmlns";a:1:{s:2:"dc";s:32:"http://purl.org/dc/elements/1.1/";}}}s:7:"pubDate";a:1:{i:0;a:6:{s:4:"data";s:29:"Mon, 03 Apr 2017 12:50:00 GMT";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";s:5:"xmlns";a:1:{s:2:"dc";s:32:"http://purl.org/dc/elements/1.1/";}}}s:4:"guid";a:1:{i:0;a:6:{s:4:"data";s:67:"http://24tv.ua/putin_prokomentuvav_vibuh_u_metro_peterburga_n801590";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";s:5:"xmlns";a:1:{s:2:"dc";s:32:"http://purl.org/dc/elements/1.1/";}}}}s:32:"http://purl.org/dc/elements/1.1/";a:2:{s:7:"creator";a:1:{i:0;a:6:{s:4:"data";s:32:"Телеканал новин 24";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";s:5:"xmlns";a:1:{s:2:"dc";s:32:"http://purl.org/dc/elements/1.1/";}}}s:4:"date";a:1:{i:0;a:6:{s:4:"data";s:20:"2017-04-03T12:50:00Z";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";s:5:"xmlns";a:1:{s:2:"dc";s:32:"http://purl.org/dc/elements/1.1/";}}}}}}i:47;a:7:{s:4:"data";s:54:"\n      \n      \n      \n      \n      \n      \n      \n    ";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";s:5:"xmlns";a:1:{s:2:"dc";s:32:"http://purl.org/dc/elements/1.1/";}s:5:"child";a:2:{s:0:"";a:5:{s:5:"title";a:1:{i:0;a:5:{s:4:"data";s:122:"У Норвегії створили спеціальне сховище даних до &quot;Судного дня&quot;";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:4:"link";a:1:{i:0;a:6:{s:4:"data";s:86:"http://24tv.ua/u_norvegiyi_stvorili_spetsialne_shovishhe_danih_do_sudnogo_dnya_n801573";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";s:5:"xmlns";a:1:{s:2:"dc";s:32:"http://purl.org/dc/elements/1.1/";}}}s:11:"description";a:1:{i:0;a:6:{s:4:"data";s:2192:"<img align=''left'' vspace=''5'' hspace=''10'' src=''https://imagecdn1.luxnet.ua/tv24/resources/photos/news/640_DIR/201704/801573.jpg''>\n \n \n  На норвезькому острові Шпіцберген створили бібліотеку, в якій будуть зберігатися найважливіші книги, документи та дані на випадок якоїсь всесвітньої катастрофи. \n  Про це пишуть на Gizmodo. \n  Норвезька компанія Piql вже опрацьовує цифрові дані зі "Сховища Судного дня" і переводить їх в аналоговий формат з використанням спеціально розробленої плівки. Такі дані, як зазначають розробники, можуть зберігатися 500-1000 років. За словами співробітника Piql, Катрін Лоен Томсен, процес схожий на перетворення даних у "великій QR-код на плівці". \n   \n  Будь-яка країна має можливість завантажити будь-який контент на сервери, які пізніше передаються на спеціальну плівку. Пізніше вона поміщається в захищену коробку, яка відправляється в сховище. Світовий "арктичний архів" побудований в скелі і відкритий для урядів та дослідних центрів з усього світу як місце для зберігання записів. \n  На даний момент свої дані передали лише Бразилія і Мексика. Вони представили в першу чергу історичні документи, що стосуються життя індіанських племен. \n  До речі, в цій скелі також зберігається близько мільйона зразків насіння з майже кожної країни на Землі.";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";s:5:"xmlns";a:1:{s:2:"dc";s:32:"http://purl.org/dc/elements/1.1/";}}}s:7:"pubDate";a:1:{i:0;a:6:{s:4:"data";s:29:"Mon, 03 Apr 2017 12:49:00 GMT";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";s:5:"xmlns";a:1:{s:2:"dc";s:32:"http://purl.org/dc/elements/1.1/";}}}s:4:"guid";a:1:{i:0;a:6:{s:4:"data";s:86:"http://24tv.ua/u_norvegiyi_stvorili_spetsialne_shovishhe_danih_do_sudnogo_dnya_n801573";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";s:5:"xmlns";a:1:{s:2:"dc";s:32:"http://purl.org/dc/elements/1.1/";}}}}s:32:"http://purl.org/dc/elements/1.1/";a:2:{s:7:"